import React from 'react';
/**
 * Just a wrapper on standard html anchor to **safely** redirect user to the 3rd party website
 */
export const ExternalLink = ({
    href,
    children,
    ...props
}) => (React.createElement("a", {
    href: href,
    target: "_blank",
    rel: "noopener noreferrer",
    ...props
}, children));