import React from 'react';
import {
    useEffect
} from 'react';
import {
    useLocation
} from 'react-router-dom';
/**
 * App Wrapper that reacts to route change and scrolls page to top
 */
export const ScrollToTop = ({
    children
}) => {
    const {
        pathname
    } = useLocation();
    useEffect(goTop, [pathname]);
    return React.createElement(React.Fragment, null, children);
};
/**
 * Dynamically scrolls page to top
 */
export const goTop = () => {
    /* It's like this, because apparently not all browsers support ScrollToOptions API. */
    try {
        window.scrollTo({
            top: 0,
            left: 0,
            behavior: 'smooth',
        });
    } catch {
        window.scrollTo(0, 0);
    }
};