import React, {
    useContext
} from 'react';
/** @private Use PermissionsProvider/PermissionsConsumer or usePermissions hook */
export const PermissionsContext = React.createContext({
    permissions: [],
});
export const PermissionsProvider = PermissionsContext.Provider;
export const PermissionsConsumer = PermissionsContext.Consumer;
export const usePermissions = () => {
    const context = useContext(PermissionsContext);
    if (!context) {
        throw new Error('This component should be wrapped by PermissionsContext.Provider');
    }
    return context;
};