const hasAtLeastOnePermission = (list) => Boolean(list && list.length > 0);
export function hasRequiredPermissions(userPermissions, allOf, anyOf) {
    if (!hasAtLeastOnePermission(userPermissions)) {
        return !hasAtLeastOnePermission(allOf) && !hasAtLeastOnePermission(anyOf);
    }
    if (hasAtLeastOnePermission(allOf) && !allOf.every((p) => userPermissions.includes(p))) {
        return false;
    }
    if (hasAtLeastOnePermission(anyOf) && !anyOf.some((p) => userPermissions.includes(p))) {
        return false;
    }
    return true;
}