/**
 * Gets a parameter value from location URL
 * @example
 * URL: `http://ieltsregistration.britishcouncil.org/app?organisation=UEFA`
 * getUrlSearchParam('organisation') -> `UEFA`
 * @param searchedParam Parameter name
 */
export function getUrlSearchParam(searchedParam) {
    const search = decodeURIComponent(window.location.search);
    const matches = search.match(new RegExp(`[?&]${searchedParam}=([^?&#]*)`, 'i'));
    if (matches) {
        return decodeURI(matches[1]);
    }
    return null;
}