export const isChangeEvent = (value) => {
    if (typeof value !== 'object' || value === null) {
        return false;
    }
    const e = value;
    return Boolean(e.nativeEvent && e.nativeEvent instanceof Event && e.target);
};
export const isChangeEventWithValue = (value) => {
    return isChangeEvent(value) && 'value' in value.target;
};