/** Use it when you need to add opacity to hex color  */
export const hexWithOpacity = (color, opacity) => {
    const colorWithoutHash = color.includes('#') ? color.slice(1) : color;
    // Convert the opacity value to an integer between 0 and 255
    const opacityValue = Math.round(opacity * 255);
    // Combine the RGB values and opacity value into a single hex string
    const hex = `#${colorWithoutHash}${opacityValue.toString(16).padStart(2, '0')}`;
    return hex;
};