import {
    v4 as uuid
} from 'uuid';
export const requestIdHeader = 'X-Request-Id';
export const sessionIdHeader = 'X-Session-Id';
export const sessionIdKey = 'Session-Id';
export const setupAuditInterceptor = (axios) => {
    axios.interceptors.request.use((config) => {
        config.headers[requestIdHeader] = uuid();
        config.headers[sessionIdHeader] = getSessionId();
        return config;
    });
};
const getSessionId = () => {
    let sessionId = sessionStorage.getItem(sessionIdKey);
    if (!sessionId) {
        sessionId = uuid();
        sessionStorage.setItem(sessionIdKey, sessionId);
    }
    return sessionId;
};