/**
 * Checks if current browser is Internet Explorer (any version)
 */
export function isIE() {
    const ua = window.navigator.userAgent;
    const msie = ua.indexOf('MSIE ');
    if (msie > 0) {
        // IE 10 or older => return true
        return true;
    }
    const trident = ua.indexOf('Trident/');
    if (trident > 0) {
        // IE 11 => return true
        return true;
    }
    // other browser
    return false;
}