import {
    useLayoutEffect,
    useRef,
    useState
} from 'react';
export const useParentsCount = ({
    className
}) => {
    const ref = useRef(null);
    const [parentsCount, setParentsCount] = useState(0);
    useLayoutEffect(() => {
        if (ref.current) {
            const els = [];
            var el = ref.current.parentNode;
            while (el) {
                els.unshift(el);
                el = el.parentNode;
            }
            setParentsCount(els.filter((x) => x.className && x.className.split(' ').includes(className)).length);
        } else {
            setParentsCount(0);
        }
    }, [className, ref]);
    return {
        ref,
        parentsCount
    };
};