import styled from '@emotion/styled';
import React, {
    useEffect,
    useState
} from 'react';
const StyledLogo = styled.img `
  height: 30px;
`;
export const Logo = ({
    logoUrl,
    alt
}) => {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);
    useEffect(() => {
        const img = new Image();
        img.onload = () => {
            setLoading(false);
        };
        img.onerror = () => {
            setLoading(false);
            setError(true);
        };
        img.src = logoUrl;
        return () => {
            img.onload = null;
            img.onerror = null;
        };
    }, [logoUrl]);
    if (loading || error) {
        return null;
    }
    return React.createElement(StyledLogo, {
        src: logoUrl,
        alt: alt
    });
};