import styled from '@emotion/styled';
import {
    spacingFn
} from '../../theme';
import {
    theme
} from '../../theme/theme';
import {
    css
} from '@emotion/react';
export const Root = styled.div `
  display: flex;
  flex-direction: column;

  gap: ${spacingFn(2.25)};
  border-radius: ${({ theme }) => theme.spacing.sm};
  padding: ${({ theme }) => theme.spacing.lg};

  ${({ theme, elevation }) => theme.elevation[elevation]};
  background: ${({ theme, color }) => theme.colors.segment[color]};

  ${({ selectable }) => (selectable ? Selectable : null)};
  ${({ selectable, selected }) => (selectable && selected ? Selected : null)};
`;
const Selectable = css `
  cursor: pointer;
`;
const Selected = css `
  border: 2px solid ${theme.colors.darkBlue};
  box-shadow: none;
`;