import React, {
    useRef,
    useEffect
} from 'react';
import {
    useCombinedRefs
} from '@britishcouncil/react-common';
import {
    withoutAdditionalProps
} from './propHelpers';
export const Checkbox = ({
    indeterminate = false,
    inputRef,
    onBlur,
    onChange,
    value,
    ...props
}) => {
    const innerRef = useRef(null);
    useEffect(() => {
        if (innerRef.current) {
            innerRef.current.indeterminate = indeterminate;
        }
    }, [indeterminate]);
    return (React.createElement("input", { ...withoutAdditionalProps(props),
        checked: Boolean(value),
        type: "checkbox",
        ref: useCombinedRefs(inputRef, innerRef),
        onBlur: () => onBlur(),
        onChange: e => onChange(Boolean(e.target.checked)),
        value: ""
    }));
};