import React from 'react';
import {
    withoutAdditionalProps
} from './propHelpers';
export const Radio = ({
    onBlur,
    onChange,
    checkValue,
    inputRef,
    value,
    ...props
}) => {
    return (React.createElement("input", { ...withoutAdditionalProps(props),
        checked: checkValue === value,
        type: "radio",
        ref: inputRef,
        onBlur: () => onBlur(),
        onChange: () => onChange(checkValue),
        value: value !== undefined ? `${value}` : ''
    }));
};