import React from 'react';
import {
    withoutAdditionalProps
} from './propHelpers';
export const TextArea = ({
    onBlur,
    onChange,
    textAreaRef,
    ...props
}) => {
    return (React.createElement("textarea", { ...withoutAdditionalProps(props),
        ref: textAreaRef,
        onBlur: () => onBlur(),
        onChange: e => onChange(e.target.value.toString())
    }));
};