import React from 'react';
import {
    withoutAdditionalProps
} from './propHelpers';
export const TextInput = ({
    onBlur,
    onChange,
    inputRef,
    ...props
}) => {
    return (React.createElement("input", { ...withoutAdditionalProps(props),
        ref: inputRef,
        onBlur: () => onBlur(),
        onChange: e => onChange(e.target.value.toString())
    }));
};