import {
    useFormik
} from 'formik';
import {
    useFormFields
} from '.';
export const useForm = (schema, initialValues, onSubmit,
    /** Any extended settings that needs to be passed to Formik */
    formikSettings) => {
    const formik = useFormik({
        initialValues,
        onSubmit,
        validationSchema: schema,
        enableReinitialize: true,
        ...(formikSettings || {}),
    });
    const fields = useFormFields(formik);
    return {
        formik,
        fields
    };
};