export const CTOR_BLACKLIST = [Date, RegExp];
export const isCtorBlacklisted = (obj) => CTOR_BLACKLIST.some(ctor => Object.prototype.toString.call(obj) === `[object ${ctor.name}]`);
export const isObject = (value) => value !== null && typeof value === 'object';
export const isPrimitive = (value) => !isObject(value) || isCtorBlacklisted(value);
export const isPrimitivesArray = (value) => Array.isArray(value) && isPrimitive(value[0]);
export const getFieldName = (baseName, key, isArray) => baseName ? (isArray ? `${baseName}[${key}]` : `${baseName}.${key}`) : key;