const mapValidationResponse = (responseData) => {
    const errors = {};
    const responseErrors = (responseData && responseData.errors) || [];
    for (const {
            propertyName,
            message
        } of responseErrors) {
        if (propertyName in errors) {
            errors[propertyName] += `\n${message}`;
        } else {
            errors[propertyName] = message;
        }
    }
    return errors;
};
export default mapValidationResponse;