export const mapToRulesFieldName = (path) => path.replace(/\[[0-9]+\]/g, '');
// Do not use !value, we have to include 0
export const isComparableNumber = (value) => value !== undefined &&
    value !== null &&
    value !== '' &&
    !Array.isArray(value) &&
    typeof value !== 'boolean' &&
    !isNaN(Number(value));