import {
    DateTime
} from 'luxon';
/**
 * Formats time nicely for the user. Basically gets rid of seconds from longer time form
 * @param time
 */
export const userFriendlyTime = (time, noTimeChar = '') => {
    if (!time) {
        return noTimeChar;
    }
    const splittedTime = time.split(':');
    return `${splittedTime[0] ?? time}:${splittedTime[1] ?? '00'}`;
};
/**
 * Returns locale date in format "DD month YYYY", e.g. "24 lipca 2021"
 * @param date ISO string or Date object
 */
export const getLocaleDate = (date, format) => {
    const defaultLocalesFormat = {
        timeZone: 'UTC',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
    };
    const dateOnly = typeof date === 'string' ? date.split('T')[0] : DateTime.fromJSDate(date).toISODate();
    return new Date(dateOnly).toLocaleDateString(undefined, { ...defaultLocalesFormat,
        ...format
    });
};