import React, {
    useState
} from 'react';
import styled from '@emotion/styled';
const StyledLink = styled.a `
  display: block;

  :hover {
    cursor: pointer;
  }
`;
export const ExpandoText = ({
    initiallyExpanded = false,
    expandText = '+ Expand',
    collapseText = '- Collapse',
    className,
    children,
    ...props
}) => {
    const [expanded, setExpanded] = useState(initiallyExpanded);
    if (expanded) {
        return (React.createElement("div", { ...props,
                className: className
            },
            children,
            React.createElement(StyledLink, {
                className: "expando-control expanded",
                onClick: () => setExpanded(false)
            }, collapseText)));
    }
    return (React.createElement("div", { ...props,
            className: className
        },
        React.createElement(StyledLink, {
            className: "expando-control collapsed",
            onClick: () => setExpanded(true)
        }, expandText)));
};