import React from 'react';
import styled from '@emotion/styled';
import {
    GoLinkExternal
} from 'react-icons/go';
const i18nDefault = {
    title: '(opens new window)',
};
const StyledA = styled.a `
  & > svg {
    vertical-align: middle;
    margin-left: 4px;

    [dir='rtl'] & {
      margin-left: 0;
      margin-right: 4px;
    }
  }
`;
/**
 * Just a wrapper on standard html anchor to **safely** redirect user to 3rd party site
 */
export const ExternalLink = ({
    href,
    querystring,
    showIcon = true,
    children,
    i18n = i18nDefault,
    ...props
}) => {
    return (React.createElement(StyledA, {
            href: querystring ? href.concat(querystring) : href,
            target: "_blank",
            rel: "noopener noreferrer",
            title: i18n.title,
            ...props
        },
        children,
        showIcon ? React.createElement(GoLinkExternal, {
            "aria-hidden": "true"
        }) : null));
};