import React from 'react';
import {
    FormattedNumber
} from 'react-intl';
/** Must be wrapped with IntlProvider */
export const FormattedPrice = ({
    value,
    currencyIsoCode,
    className,
    showFraction = false,
}) => {
    if (value === undefined) {
        return null;
    }
    return (React.createElement("span", {
            className: className
        },
        React.createElement(FormattedNumber, {
            value: value,
            minimumFractionDigits: showFraction ? 2 : Number.isInteger(value) ? 0 : 2,
            maximumFractionDigits: 2
        }),
        ' ',
        currencyIsoCode));
};