import React from 'react';
import cn from 'classnames';
/** Very **simple** ProgressBar based on Bootstrap template */
export const SimpleProgressBar = ({
    percentage,
    striped,
    animated,
    intent = 'default',
    ...props
}) => {
    const percentVal = percentage > 100 ? 100 : percentage < 0 ? 0 : percentage;
    const value = percentVal + '%';
    return (React.createElement("div", {
            className: "progress"
        },
        React.createElement("div", { ...props,
            className: cn(`progress-bar progress-bar-${intent}`, {
                'progress-bar-striped': striped,
                active: striped && animated,
            }),
            role: "progressbar",
            "aria-valuetext": value,
            "aria-valuenow": percentVal,
            "aria-valuemin": 0,
            "aria-valuemax": 100,
            style: {
                width: value
            }
        })));
};