import React from 'react';
import cn from 'classnames';
/**
 * This is a usability wrapper for the most frequently used container example in b2c apps.
 * Use it whether you want nicely organize page without any strange adjustments
 */
export const SimpleContainer = ({
    role,
    children,
    centered,
    size,
    ...props
}) => {
    const colClasses = cn('col-xs-12', {
        'center-block': centered,
        'col-md-8': size === 'medium',
        'col-md-6': size === 'small',
        'col-md-12': size === 'large',
    });
    return (React.createElement("div", {
            role: role,
            ...props,
            className: cn('container', props.className)
        },
        React.createElement("div", {
                className: "row"
            },
            React.createElement("div", {
                className: colClasses
            }, children))));
};