import React from 'react';
import {
    useMatch
} from 'react-router-dom';
import classnames from 'classnames';
export const NavItem = ({
    location,
    end = false,
    children,
    className
}) => {
    const match = useMatch({
        path: location,
        end
    });
    return React.createElement("span", {
        className: classnames(className, {
            active: !!match
        })
    }, children);
};