import React from 'react';
/**
 * Use this loader if your app is using base loading solution.
 * It means that there is already loader in the `index.html` page.
 * It does not have any styles
 */
export function IntegratedLoader(props) {
    const loader = props.loader || 'lds-dual-ring';
    return (React.createElement("div", {
            className: "loader-container",
            "aria-label": "Loading contents... Please wait"
        },
        React.createElement("div", {
            className: loader
        })));
}