import React from 'react';
import cn from 'classnames';
/**
 * Basic layout of the Solas application. It has to be a normal React component to work with the React Router
 */
export const SolasLayout = ({
    children,
    ieltsBranding,
    slogan,
    theme,
    flex,
    className,
    ...props
}) => {
    const classes = cn({
        ['bc-subtheme-' + theme]: theme,
        [slogan + '-slogan']: slogan,
        'ielts-branding': ieltsBranding,
        flex: flex,
    }, className);
    return (React.createElement("div", { ...props,
        className: classes
    }, children));
};